function handleResponse(e){"success"===e.status?Swal.fire({icon:"success",title:"¡Éxito!",text:e.message}).then(()=>{location.reload()}):Swal.fire({icon:"error",title:"Error",text:e.message})}document.getElementById("formCrearCategoria").addEventListener("submit",function(e){e.preventDefault();const t=new FormData(this);fetch("/gestion/app/controller/CategoriaController.php?action=crearCategoria",{method:"POST",body:t}).then(e=>e.json()).then(handleResponse)}),document.getElementById("formEditCategoria").addEventListener("submit",function(e){e.preventDefault();const t=new FormData(this);fetch("/gestion/app/controller/CategoriaController.php?action=editarCategoria",{method:"POST",body:t}).then(e=>e.json()).then(handleResponse)}),document.getElementById("formDeleteCategoria").addEventListener("submit",function(e){e.preventDefault();const t=new FormData(this);fetch("/gestion/app/controller/CategoriaController.php?action=eliminarCategoria",{method:"POST",body:t}).then(e=>e.json()).then(handleResponse)});const editModal=document.getElementById("editModal");editModal.addEventListener("show.bs.modal",e=>{const t=e.relatedTarget;document.getElementById("edit-id").value=t.getAttribute("data-id"),document.getElementById("edit-nombre").value=t.getAttribute("data-nombre")});const deleteModal=document.getElementById("deleteModal");deleteModal.addEventListener("show.bs.modal",e=>{const t=e.relatedTarget;document.getElementById("delete-id").value=t.getAttribute("data-id"),document.getElementById("deleteMessage").textContent=`¿Está seguro de que desea eliminar la categoría "${t.getAttribute("data-nombre")}"?`});